export default function yargs(args) {
  const out = { _: [] };
  for (let i = 0; i < args.length; i++) {
    const a = args[i];
    if (a.startsWith('--')) {
      const k = a.slice(2);
      const v = args[i + 1] && !args[i + 1].startsWith('--') ? args[++i] : true;
      out[k] = v === 'false' ? false : v === 'true' ? true : v;
    } else {
      out._.push(a);
    }
  }
  out.option = () => out;
  out.help = () => ({ argv: out });
  return out;
}
export function hideBin(argv) {
  return argv.slice(2);
}
